import React from 'react';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardMedia,
  CardContent,
  Paper,
} from '@mui/material';
import { styled } from '@mui/system';

const DashboardContainer = styled(Box)({
  padding: '20px',
  marginTop: '80px',
});

const StatsCard = styled(Paper)({
  padding: '20px',
  textAlign: 'center',
  backgroundColor: '#f5f5f5',
  boxShadow: '0px 1px 3px rgba(0, 0, 0, 0.2)',
});

const Dashboard = () => {
  // Dummy data for site statistics
  const stats = [
    { title: 'Total Sales', value: '$25,000' },
    { title: 'Total Orders', value: '1,200' },
    { title: 'Active Users', value: '320' },
    { title: 'New Signups', value: '85' },
  ];

  // Dummy data for featured products
  const products = [
    {
      id: 1,
      name: 'Oversized Tee - Men',
      image: 'https://via.placeholder.com/150',
      price: '$30',
    },
    {
      id: 2,
      name: 'Oversized Tee - Women',
      image: 'https://via.placeholder.com/150',
      price: '$30',
    },
    {
      id: 3,
      name: 'Premium Hoodie',
      image: 'https://via.placeholder.com/150',
      price: '$45',
    },
    {
      id: 4,
      name: 'Casual Sweatshirt',
      image: 'https://via.placeholder.com/150',
      price: '$40',
    },
  ];

  return (
    <DashboardContainer>
      <Typography variant="h4" gutterBottom>
        Dashboard Overview
      </Typography>
      {/* Statistics Section */}
      <Grid container spacing={3}>
        {stats.map((stat, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <StatsCard>
              <Typography variant="h6" gutterBottom>
                {stat.title}
              </Typography>
              <Typography variant="h4">{stat.value}</Typography>
            </StatsCard>
          </Grid>
        ))}
      </Grid>

      {/* Featured Products Section */}
      <Box sx={{ marginTop: '40px' }}>
        <Typography variant="h5" gutterBottom>
          Featured Products
        </Typography>
        <Grid container spacing={3}>
          {products.map((product) => (
            <Grid item xs={12} sm={6} md={3} key={product.id}>
              <Card>
                <CardMedia
                  component="img"
                  alt={product.name}
                  height="150"
                  image={product.image}
                />
                <CardContent>
                  <Typography variant="h6">{product.name}</Typography>
                  <Typography variant="body2" color="text.secondary">
                    {product.price}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </DashboardContainer>
  );
};

export default Dashboard;
