import React from 'react';
import { AppBar, Toolbar, Button, Box } from '@mui/material';
import { styled } from '@mui/system';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import baaMbooLogo from '../assets/mainlogo1.png';

const PageContainer = styled(Box)(({ theme }) => ({
  maxWidth: '1440px',
  width: '100%',
  margin: '0 auto',
  padding: { xs: '0 10px', sm: '0 20px', md: '0 40px' },
}));

const StyledAppBar = styled(AppBar)(({ theme }) => ({
  backgroundColor: '#000000',
  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
}));

const StyledButton = styled(Button)(({ theme }) => ({
  color: '#ffffff',
  position: 'relative',
  padding: { xs: '6px 12px', sm: '8px 16px' }, // Responsive padding
  fontWeight: 500,
  textTransform: 'none',
  fontSize: { xs: '0.9rem', sm: '1rem' }, // Responsive font size
  '&:hover': {
    color: '#ffffff',
    backgroundColor: 'transparent',
  },
  '&:after': {
    content: '""',
    position: 'absolute',
    width: '0',
    height: '2px',
    bottom: '0',
    left: '50%',
    backgroundColor: '#28b463',
    transition: 'width 0.3s ease, left 0.3s ease',
  },
  '&:hover:after': {
    width: '100%',
    left: '0',
  },
  [theme.breakpoints.down('sm')]: {
    padding: '4px 8px',
  },
}));

const CartButton = styled(Button)(({ theme }) => ({
  color: '#ffffff',
  padding: { xs: '6px', sm: '8px' },
  minWidth: 'auto',
  '&:hover': {
    color: '#28b463',
    backgroundColor: 'transparent',
  },
}));

const LogoImage = styled('img')(({ theme }) => ({
  height: { xs: '30px', sm: '40px' }, // Responsive height
  width: { xs: '150px', sm: '200px' }, // Responsive width
  cursor: 'pointer',
}));

const Navbar = () => {
  return (
    <StyledAppBar position="static">
      <PageContainer>
        <Toolbar sx={{ justifyContent: 'space-between', alignItems: 'center', minHeight: { xs: '56px', sm: '64px' } }}>
          <Box>
            <a href="#home" style={{ textDecoration: 'none' }}>
              <LogoImage src={baaMbooLogo} alt="Baa M Boo Logo" />
            </a>
          </Box>
          <Box
            sx={{
              flexGrow: 1,
              display: { xs: 'none', md: 'flex' }, // Hide links on small screens
              justifyContent: 'center',
              gap: { xs: '10px', sm: '20px', md: '30px' },
            }}
          >
            <StyledButton href="#home">Home</StyledButton>
            <StyledButton href="#men">Men</StyledButton>
            <StyledButton href="#women">Women</StyledButton>
            <StyledButton href="#contact">Contact</StyledButton>
          </Box>
          <Box>
            <CartButton href="#cart">
              <ShoppingCartIcon sx={{ fontSize: { xs: '24px', sm: '28px' } }} />
            </CartButton>
          </Box>
        </Toolbar>
      </PageContainer>
    </StyledAppBar>
  );
};

export default Navbar;
