import React from 'react';
import { Box, Typography, Button } from '@mui/material';
import { styled } from '@mui/system';

// Styled Components for Hero Section
const HeroSection = styled(Box)({
  backgroundImage: `url('https://images.unsplash.com/photo-1523381210434-271e8be1f52b?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80')`, // Dummy hero image
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  height: '90vh',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
  alignItems: 'center',
  color: '#ffffff',
  textAlign: 'center',
  padding: '0 40px',
  position: 'relative',
  '&:before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.6)', // Darker overlay for better contrast
  },
});

const HeroButton = styled(Button)({
  backgroundColor: '#ffffff',
  color: '#000000',
  padding: '16px 50px',
  fontSize: '1.3rem',
  fontWeight: 'bold',
  borderRadius: '50px',
  textTransform: 'none',
  boxShadow: '0 4px 15px rgba(255, 255, 255, 0.4)',
  transition: 'background-color 0.3s ease, transform 0.2s ease, box-shadow 0.3s ease',
  '&:hover': {
    backgroundColor: '#e0e0e0',
    transform: 'scale(1.05)',
    boxShadow: '0 6px 20px rgba(255, 255, 255, 0.6)',
  },
});

const Home = () => {
  return (
    <Box sx={{ backgroundColor: '#000000', minHeight: '100vh' }}>
      {/* Hero Section */}
      <HeroSection>
        <Typography
          variant="h1"
          sx={{
            fontSize: { xs: '3rem', md: '5.5rem' },
            zIndex: 1,
            mb: 3,
            fontWeight: 'bold',
            letterSpacing: '3px',
            color: '#ffffff',
            textShadow: '0 2px 10px rgba(0, 0, 0, 0.5)', // Subtle shadow for depth
          }}
        >
          Premium Oversized Tees
        </Typography>
        <Typography
          variant="h5"
          sx={{
            zIndex: 1,
            mb: 5,
            maxWidth: '900px',
            fontWeight: 300,
            lineHeight: 1.6,
            color: '#ffffff',
            fontSize: { xs: '1.2rem', md: '1.5rem' },
          }}
        >
          Discover the ultimate blend of comfort and style with Baa M Booâ€™s exclusive collection of oversized tees for men and women. Crafted with premium materials for a perfect fit that lasts. Elevate your wardrobe with our curated designs, made to stand out and feel exceptional.
        </Typography>
        <Box sx={{ zIndex: 1 }}>
          <HeroButton href="#shop">Shop Now</HeroButton>
        </Box>
      </HeroSection>
    </Box>
  );
};

export default Home;
