import React from 'react';
import { AppBar, Toolbar, Button, Box } from '@mui/material';
import { styled } from '@mui/system';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';

import baaMbooLogo from '../assets/mainlogo1.png';

const StyledAppBar = styled(AppBar)({
  backgroundColor: '#000000',
  padding: '10px 40px', // Increased padding for better spacing
  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)', // Subtle shadow for depth
});

const StyledButton = styled(Button)({
  color: '#ffffff',
  position: 'relative', // For the underline effect
  padding: '8px 16px',
  fontWeight: 500,
  textTransform: 'none',
  fontSize: '1rem',
  '&:hover': {
    color: '#ffffff',
    backgroundColor: 'transparent',
  },
  '&:after': {
    content: '""',
    position: 'absolute',
    width: '0',
    height: '2px',
    bottom: '0',
    left: '50%',
    backgroundColor: '#28b463', // Your brand color for underline
    transition: 'width 0.3s ease, left 0.3s ease', // Smooth animation
  },
  '&:hover:after': {
    width: '100%',
    left: '0', // Expands from center to full width
  },
});

const CartButton = styled(Button)({
  color: '#ffffff',
  padding: '8px',
  minWidth: 'auto', // Reduces button width for icon
  '&:hover': {
    color: '#28b463', // Matches your brand color
    backgroundColor: 'transparent',
  },
});

const LogoImage = styled('img')({
  height: '40px',
  width: '200px',
  cursor: 'pointer', // Makes logo clickable
});

const Navbar = () => {
  return (
    <StyledAppBar position="static">
      <Toolbar sx={{ justifyContent: 'space-between', alignItems: 'center' }}>
        {/* Logo on the Left */}
        <Box>
          <a href="#home" style={{ textDecoration: 'none' }}>
            <LogoImage src={baaMbooLogo} alt="Baa M Boo Logo" />
          </a>
        </Box>

        {/* Centered Navigation Links */}
        <Box
          sx={{
            flexGrow: 1,
            display: 'flex',
            justifyContent: 'center',
            gap: '30px', // Space between buttons
          }}
        >
          <StyledButton href="#home">Home</StyledButton>
          <StyledButton href="#men">Men</StyledButton>
          <StyledButton href="#women">Women</StyledButton>
          <StyledButton href="#contact">Contact</StyledButton>
        </Box>

        {/* Cart Icon on the Right */}
        <Box>
          <CartButton href="#cart">
            <ShoppingCartIcon sx={{ fontSize: '28px' }} />
          </CartButton>
        </Box>
      </Toolbar>
    </StyledAppBar>
  );
};

export default Navbar;
